mapper.select:

select bno, title, content, writer, register_date
from (
select /*+ INDEX_DESC(notice XPK공지사항) */ rownum rn, bno, title, content, show_yn, writer, register_date
from notice where show_yn='y' AND rownum <= #{pageNum} * #{amount}
)
where rn > (#{pageNum} - 1) * #{amount}

mapper.delete:
update notice
set show_yn='n'
where bno=#{bno}
